<?phpfunction SENDSMS($to ,$msg ,$type) {	if(!preg_match ('/^((\(?0\d{4}\)?\s?\d{3}\s?\d{3})|(\(?0\d{3}\)?\s?\d{3}\s?\d{4})|(\(?0\d{2}\)?\s?\d{4}\s?\d{4}))(\s?\#(\d{4}|\d{3}))?$/', $to)) {		error_log('Phone number not correct! ('.$to.')', 0);		return false;	}	//$to = '447896592291';	$To = str_replace('+' , '' , '44'.substr($to, 1));	switch($type){		case 'new_order_user' :			$TEXT = "Thanks for your order at Just-FastFood.com. Your Order Is On Its Way!";			$TEXT .= " Order Id : ".$msg['order_id']." ";			break;        case 'new_staff_added' :			$TEXT = "Dear ".$msg['staff_name']."! I'm pleased to inform you that you have now been added as one of our delivery drivers. We look forward to working with you. http://www.just-fastfood.com";			break;		case 'assign_order_guy' :            $TEXT = "Alert: ID:".$_SESSION['CURRENT_ORDER_ID'].', '.$_SESSION['CURRENT_MENU'];            $TEXT .= ". Delv Addr: ".$_SESSION['user_address']. ' Phone: '. $_SESSION['user_phoneno'];            $TEXT .=  " ".(is_user_corporate($_SESSION['userId']) == 'true' ? ". Company Order" : "");			$TEXT .= "., ".key(json_decode($msg['order_postcode'], true));			$order = json_decode($msg['order_details'] ,true);			foreach($order as $k=> $val) {				if($k != 'TOTAL'){					$TEXT .= ' '.$val['QTY'].'x '.$val['NAME'] .', ';				}			}			$TEXT = substr($TEXT,0,-2);			if(!empty($msg['order_note'])) {				$TEXT .= ". O.N: ".$msg['order_note'];			}            $TEXT .= '.,'.$msg['order_payment_type'];      if($msg['order_payment_type'] !== 'paypal') {        $TEXT .= ' Amount : £'.$_SESSION['CART_SUBTOTAL'];      }      $TEXT.= ' Arrival_Time: '.($_SESSION['order_delivery_time'] == 'ASAP' ? 'ASAP' : date('g:i a', strtotime($_SESSION['order_delivery_time'])));			break;		case 'admin_alert' :			$TEXT = "Alert: ID:".$_SESSION['CURRENT_ORDER_ID'].', '.$_SESSION['CURRENT_MENU'];			$TEXT .= ". Delv Addr: ".$_SESSION['user_address'].' ';            $TEXT .=  " ".(is_user_corporate($_SESSION['userId']) == 'true' ? ". Company Order" : "");            $TEXT .= "., ".$_SESSION['CURRENT_POSTCODE'];			$order = $_SESSION['CART'];			foreach($order as $k=> $val) {				if($k != 'TOTAL'){					$TEXT .= ' '.$val['QTY'].'x '.$val['NAME'] .', ';				}			}			$TEXT = substr($TEXT,0,-2);            if(!empty($msg['order_note'])) {                $TEXT .= ". O.N: " .$msg['order_note'] .', ';            }            $TEXT .= '.,'.$msg['order_payment_type'];      $TEXT .= ' Total : '.$_SESSION['CART_SUBTOTAL'];      $TEXT.= ' Order_Arrival_Time: '.($_SESSION['order_delivery_time'] == 'ASAP' ? 'ASAP' : date('g:i a', strtotime($_SESSION['order_delivery_time'])));      unset($_SESSION['CART']);      unset($_SESSION['DELIVERY_CHARGES']);			break;	}	//require_once "Services/Twilio.php";	//$AccountSid = "AC1a8187b8a142c83ad604ef9a0690e2bd";	//$AuthToken = "616fd8718af466f7ce2723f6c5d2db9f";   // $client = new Services_Twilio($AccountSid, $AuthToken);    require_once "Services/Nexmo/NexmoMessage.php";    $apiKey = "6aeaa543";    $apiSecret = "aece4e30";    $nexmo_sms = new NexmoMessage($apiKey, $apiSecret);	//$From = '+441157070423';    $From = '+447480635091';  //$From = 'JustFastFood';	$message = $TEXT;	if(strlen($message) > 1660){		$messages = str_split($message , 352);		$how_many = count($messages);		foreach($messages as $index => $message){			$msg_number = ($index + 1);			$message = "(".$msg_number."/".$how_many.") ".$message;			 $nexmo_sms->sendText($To, $From, $message);		}	}	else{        $nexmo_sms->sendText($To, $From, $message);	}}?>